catalog_script_client.script:
/* global onChange, g_form */
function onChange(control, oldValue, newValue, isLoading) {
console.log('method', newValue);
  if (isLoading) {
    return;
  }
  g_form.clearValue('function');
  var paramsWithValues = 0;
  var params = {};
  var method = g_form.getValue('method').split(':')[0];
  var param1 = g_form.getValue('method').split(':')[1];
  var param2 = g_form.getValue('method').split(':')[2];
  var param3 = g_form.getValue('method').split(':')[3];
  var param4 = g_form.getValue('method').split(':')[4];
  var functionStr = 'g_form.' + method;
  var functionParams = '';
  if (paramsWithValues === 0) {
    functionParams = '()';
  }
  if (param1) {
    functionParams = '("' + param1 + '")';
    g_form.setValue('param1', param1);
    g_form.clearValue('param2');
    g_form.clearValue('param3');
    g_form.clearValue('param4');
  }
  if (param2) {
    functionParams = '("' + param1 + '","' + param2 + '");';
    g_form.setValue('param1', param1);
    g_form.setValue('param2', param2);
    g_form.clearValue('param3');
    g_form.clearValue('param4');
  }
  if (param3) {
    functionParams = '("' + param1 + '","' + param2 + '","' + param3 + '");';
    g_form.setValue('param1', param1);
    g_form.setValue('param2', param2);
    g_form.setValue('param3', param3);
    g_form.clearValue('param4');
  }
  if (param4) {
    functionParams = '("' + param1 + '","' + param2 + '","' + param3 + '","' + param4 + '");';
    g_form.setValue('param1', param1);
    g_form.setValue('param2', param2);
    g_form.setValue('param3', param3);
    g_form.setValue('param4', param4);
  }

  g_form.setValue('function', functionStr + functionParams);
}
