catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	try{
		console.log('in getCategories');
		var ga = new GlideAjax('x_8821_glide_utils.makeItem');
		ga.addParam('sysparm_name', 'getCategories');
		ga.addParam('sysparm_obj', JSON.stringify({
			instance: g_form.getValue('instance'),
			catalog: newValue
		}));
		ga.getXML(function(response){
			var responseDocument = response.responseXML.documentElement;
			var answer = responseDocument.getAttribute('answer');
			var serverObj = JSON.parse(answer);
			console.log(serverObj);
			g_form.clearOptions('category');
			for (var x = 0 ;x<serverObj.responseBody.length;x++){
				console.log('adding' + serverObj.responseBody[x]);
				g_form.addOption('category',serverObj.responseBody[x].sys_id,serverObj.responseBody[x].title,x);
			}
		});
	}catch(e){
		console.log(e);
	}

}
