sys_ui_script.script:
var x_8821_glide_utils = x_8821_glide_utils || {};

x_8821_glide_utils.wee = (function() {
	"use strict";
	return {
		weeRow: function(queryObj,callback){
			try{
				var ga = new GlideAjax('x_8821_glide_utils.GlideUtils');
				ga.addParam('sysparm_name', 'weeRow');
				ga.addParam('sysparm_obj', JSON.stringify(queryObj));
				ga.getXML(function(response) {
					var responseDocument = response.responseXML.documentElement;
					var answer = responseDocument.getAttribute('answer');
					var serverObj = JSON.parse(answer);
					return callback(serverObj);
				});
			}catch(error){
				console.log(error);
				return error;
			}
		},
		/* set your public API here. For example:
			incrementAndReturnPrivateVar: function() {
				return private_function();
			},
 			*/
		type:  "wee"
	};
})();
